			else if (l == r) return new (_curr++) Node(l, r, null, null, v->size - 1, v->sum - x);
			else {
				int mid = l + (r - l) / 2;
				return new (_curr++) Node(l, r, insert(v->lc, l, mid, x, isDelete), insert(v->rc, mid + 1, r, x, isDelete));
